$(window).resize(function($){updateHeight()});jQuery(document).ready(function($){updateHeight()});function updateHeight(){var width=$(window).width();if(width>767){$("#mainHero").css("height",$(window).width()*0.4)}else{$("#mainHero").css("height",$(window).width()*0.8)}var h=$("#mainHero").height();$(".affix-top").attr("data-offset-top",h+80)};